#define MINIDLNA_SERVER_STRING "TIZEN/2.3 UPnP/1.0 " SERVER_NAME "/" MINIDLNA_VERSION

/*
 states :
 0 - waiting for data to read
 1 - waiting for HTTP Post Content.
 ...
 >= 100 - to be deleted
 */
enum httpCommands {
	EUnknown = 0, EGet, EPost, EHead, ESubscribe, EUnSubscribe
};

struct upnphttp {
	int socket;
	struct in_addr clientaddr; /* client address */
	int iface;
	int state;
	char HttpVer[16];
	/* request */
	char * req_buf;
	int req_buflen;
	int req_contentlen;
	int req_contentoff; /* header length */
	enum httpCommands req_command;
	struct client_cache_s * req_client;
	const char * req_soapAction;
	int req_soapActionLen;
	const char * req_Callback; /* For SUBSCRIBE */
	int req_CallbackLen;
	const char * req_NT;
	int req_NTLen;
	int req_Timeout;
	const char * req_SID; /* For UNSUBSCRIBE */
	int req_SIDLen;
	off_t req_RangeStart;
	off_t req_RangeEnd;
	long int req_chunklen;
	uint32_t reqflags;

	/* response */

	char * res_buf;
	int res_buflen;
	int res_buf_alloclen;
	uint32_t respflags;
	/*int res_contentlen;*/
	/*int res_contentoff;*//* header length */LIST_ENTRY(upnphttp) entries;
};

